import React, { useEffect, useState } from "react";
import "./GuessGhibli.css";

type ImagePair = {
  ghibli?: string; // optional
  original: string;
  baseName: string;
  type: "ghibli" | "quiz";
};

const baseNames = [
  "question_[]",
  "question_7514",
  "question_10",
  "question_dateadd",
  "question_default",
  "question_groupby",
  "question_idea",
  "question_isnull",
  "question_keyboard",
  "question_lambda",
  "question_m",
  "question_maine",
  "question_match",
  "question_o",
  "question_pomeranian",
  "question_string",
  "salman khan",
  "bhaskar+vishnu+suresh",
  "ravi teja",
  "rashmika mandana",
  "priyanka chopra",
  "nana patekar",
  "mahesh babu",
  "jeff bezos",
  "hrithik roshan",
  "bramhanandam",
  "bhaskar",
  "raju",
  "shravan",
  "vineela+laxmi+poojita",
];

const extensions = ["jpg", "jpeg", "png"];

const GuessGhibli: React.FC = () => {
  const [userName, setUserName] = useState("");
  const [hasStarted, setHasStarted] = useState(false);
  const [imagePairs, setImagePairs] = useState<ImagePair[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showOriginal, setShowOriginal] = useState(false);
  const [timer, setTimer] = useState(15);
  const [answer, setAnswer] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [gameEnded, setGameEnded] = useState(false);

  const checkImage = (src: string): Promise<boolean> => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => resolve(true);
      img.onerror = () => resolve(false);
      img.src = src;
    });
  };

  const findValidImage = async (baseName: string): Promise<string | null> => {
    for (const ext of extensions) {
      const url = `/images/${baseName}.${ext}`;
      const exists = await checkImage(url);
      if (exists) return url;
    }
    return null;
  };

  useEffect(() => {
    const loadImages = async () => {
      const pairs: ImagePair[] = [];

      for (const base of baseNames) {
        const ghibliImg = await findValidImage(`${base}_gibili`);
        const originalImg = await findValidImage(base);

        if (originalImg) {
          pairs.push({
            ghibli: ghibliImg || undefined,
            original: originalImg,
            baseName: base,
            type: ghibliImg ? "ghibli" : "quiz",
          });
        }
      }

      setImagePairs(pairs);
    };

    loadImages();
  }, []);

  useEffect(() => {
    if (gameEnded || !hasStarted) return;

    const current = imagePairs[currentIndex];

    if (!showOriginal) {
      if (timer > 0) {
        const interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
        return () => clearInterval(interval);
      } else {
        if (current.type === "ghibli") {
          setShowOriginal(true);
        } else {
          // For quiz-type questions, just go to next question
          setShowOriginal(true);
        }
      }
    } else {
      const timeout = setTimeout(() => {
        if (currentIndex + 1 >= imagePairs.length) {
          setGameEnded(true);
        } else {
          setCurrentIndex((prev) => prev + 1);
          setTimer(15);
          setAnswer("");
          setSubmitted(false);
          setShowOriginal(false);
        }
      }, 5000);

      return () => clearTimeout(timeout);
    }
  }, [timer, showOriginal, currentIndex, imagePairs.length, gameEnded, hasStarted]);

  const handleSubmit = () => {
    setSubmitted(true);

    const correctParts = imagePairs[currentIndex].baseName
      .toLowerCase()
      .split("+")
      .map((part) => part.trim());

    const userAnswer = answer.toLowerCase().trim();

    if (correctParts.some((name) => userAnswer.includes(name))) {
      setScore((prev) => prev + 1);
    }
  };

  if (!hasStarted) {
    return (
      <div className="login-screen">
        <h2>üé® Welcome to Guess the Ghibli!</h2>
        <input
          type="text"
          placeholder="Enter your name"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
        />
        <button
          onClick={() => {
            if (userName.trim() !== "") setHasStarted(true);
          }}
        >
          Start Game
        </button>
      </div>
    );
  }

  if (imagePairs.length === 0) return <div className="loading">Loading...</div>;

  const current = imagePairs[currentIndex];

  return (
    <div className="guess-ghibli-container">
      <h1>{current.type === "ghibli" ? "Guess the Character (Ghibli Edition)" : "Quiz Mode"}</h1>
      <h2>Hello, {userName}! üëã</h2>

      {!gameEnded ? (
        <>
          <p className="timer">‚è±Ô∏è Timer: {timer}s</p>
          <div className="image-container">
            {current.type === "ghibli" && !showOriginal && (
              <img src={current.ghibli} alt="Ghibli" className="image" />
            )}

            {current.type === "ghibli" && showOriginal && (
              <>
                <img src={current.ghibli} alt="Ghibli" className="image side-by-side" />
                <img src={current.original} alt="Original" className="image side-by-side" />
              </>
            )}

            {current.type === "quiz" && (
              <img src={current.original} alt="Quiz" className="image" />
            )}
          </div>

          {!showOriginal && (
            <div className="input-section">
              <input
                type="text"
                placeholder="Enter your guess"
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                disabled={submitted}
              />
              <button onClick={handleSubmit} disabled={submitted}>
                Submit
              </button>
              {submitted && <p className="feedback">Answer submitted! Revealing soon...</p>}
            </div>
          )}

          {showOriginal && (
            <div className="result-section">
              <p>
                ‚úÖ Correct Answer: <strong>{current.baseName}</strong>
              </p>
              <p>
                üìù Your answer: <strong>{answer || "(No answer given)"}</strong>
              </p>
              <p>Current Score: {score}/{imagePairs.length}</p>
              <p>Next image in 5 seconds...</p>
            </div>
          )}
        </>
      ) : (
        <div className="result-final">
          <h2>üéâ Game Over, {userName}!</h2>
          <p>
            You scored <strong>{score}</strong> out of <strong>{imagePairs.length}</strong>
          </p>
          <p>Thanks for playing!</p>
        </div>
      )}
    </div>
  );
};

export default GuessGhibli;
